import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import SideNav from './SideNav/SideNav';
import Clientes from './Clientes/Clientes';
import Principal from './Principal/Principal';
import PorCobrar from './PorCobrar/PorCobrar';
import PorPagar from './PorPagar/PorPagar';
import Login from './Login/Login';
import './App.css';

function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

function App() {
  const location = useLocation();
  const isLoginPage = location.pathname === '/login';

  return (
    <div className="App">
      <Routes>
        {/* Rutas p√∫blicas */}
        <Route path="/login" element={<Login />} />

        {/* Rutas privadas dentro del layout con SideNav */}
        <Route
          path="/"
          element={
            <PrivateLayout>
              <Navigate to="/principal" replace />
            </PrivateLayout>
          }
        />
        <Route
          path="/clientes"
          element={
            <PrivateLayout>
              <Clientes />
            </PrivateLayout>
          }
        />
        <Route
          path="/principal"
          element={
            <PrivateLayout>
              <Principal />
            </PrivateLayout>
          }
        />
        <Route
          path="/porcobrar"
          element={
            <PrivateLayout>
              <PorCobrar />
            </PrivateLayout>
          }
        />
        <Route
          path="/porpagar"
          element={
            <PrivateLayout>
              <PorPagar />
            </PrivateLayout>
          }
        />
      </Routes>
    </div>
  );
}

export default AppWrapper;
