import React, { useState } from 'react';
import '../styles.css';

function PorCobrar() {
    const [data, setData] = useState([
        // Ejemplo de datos iniciales
        { COA: '123', DOC: 'Factura', DOC_SERIE: 'A', DOC_NRO: '001', DOC_FCH: '20230101', MON: 'S/.', CARGO_MN: '100', ABONO_MN: '50', CARGO_ME: '20', ABONO_ME: '10', STAT_CANC: 'P' },
        { COA: '456', DOC: 'Boleta', DOC_SERIE: 'B', DOC_NRO: '002', DOC_FCH: '20230102', MON: 'S/.', CARGO_MN: '200', ABONO_MN: '100', CARGO_ME: '40', ABONO_ME: '20', STAT_CANC: 'C' },
    ]);

    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

    const sortTableByColumn = (key) => {
        let direction = 'asc';
        if (sortConfig.key === key && sortConfig.direction === 'asc') {
            direction = 'desc';
        }
        setSortConfig({ key, direction });

        const sortedData = [...data].sort((a, b) => {
            if (a[key] < b[key]) {
                return direction === 'asc' ? -1 : 1;
            }
            if (a[key] > b[key]) {
                return direction === 'asc' ? 1 : -1;
            }
            return 0;
        });

        setData(sortedData);
    };

    const formatDateWithSlashes = (dateInt) => {
        if (!dateInt) {
            return '-';
        }
        try {
            const dateString = dateInt.toString().padStart(8, '0');
            if (dateString.length !== 8) {
                return '-';
            }
            return dateString.slice(0, 4) + '/' + dateString.slice(4, 6) + '/' + dateString.slice(6, 8);
        } catch (error) {
            console.error('Error al formatear fecha:', error);
            return '-';
        }
    };

    return (
        <div className="container">
            <h1 className="text-center mb-4" style={{ fontFamily: 'Poppins' }}>CUENTAS POR COBRAR DE CLIENTES LANCASTER S.A</h1>
            <table className="table table-striped table-hover table-sm">
                <thead className="table-primary">
                    <tr>
                        <th onClick={() => sortTableByColumn('COA')}>
                            COA {sortConfig.key === 'COA' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => sortTableByColumn('DOC')}>
                            Doc {sortConfig.key === 'DOC' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => sortTableByColumn('DOC_SERIE')}>
                            Serie {sortConfig.key === 'DOC_SERIE' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => sortTableByColumn('DOC_NRO')}>
                            Número {sortConfig.key === 'DOC_NRO' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                        <th onClick={() => sortTableByColumn('DOC_FCH')}>
                            Fecha {sortConfig.key === 'DOC_FCH' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                        <th>Moneda</th>
                        <th>Cargo en S/.</th>
                        <th>Abono en S/.</th>
                        <th>Cargo en $</th>
                        <th>Abono en $</th>
                        <th onClick={() => sortTableByColumn('STAT_CANC')}>
                            Estado {sortConfig.key === 'STAT_CANC' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.COA || '-'}</td>
                            <td>{item.DOC || '-'}</td>
                            <td>{item.DOC_SERIE || '-'}</td>
                            <td>{item.DOC_NRO || '-'}</td>
                            <td>{formatDateWithSlashes(item.DOC_FCH)}</td>
                            <td>{item.MON || '-'}</td>
                            <td>{item.CARGO_MN || '-'}</td>
                            <td>{item.ABONO_MN || '-'}</td>
                            <td>{item.CARGO_ME || '-'}</td>
                            <td>{item.ABONO_ME || '-'}</td>
                            <td>
                                {item.STAT_CANC === 'C' ? (
                                    <span style={{ color: 'red' }}>CANCELADO</span>
                                ) : (
                                    <span style={{ color: 'green' }}>PENDIENTE</span>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default PorCobrar;
